    private static Material simpleDustMaterial(String name, int color, MaterialIconSet materialIconSet) {
        return new Material.Builder(GregicalityModern.id(name)).color(color).iconSet(materialIconSet).dust().buildAndRegister();
    }

    private static Material simpleDustMaterial(String name, int color, MaterialIconSet materialIconSet, MaterialStack... components) {
        return simpleDustMaterial(name, color, materialIconSet).setComponents(components);
    }

    private static Material simpleDustMaterial(String name, int color, MaterialIconSet materialIconSet, String formula) {
        return simpleDustMaterial(name, color, materialIconSet).setFormula(formula);
    }
