    private static Material simpleFluidMaterial(String name, int color, int temperature, boolean hasPlasma, boolean hasLiquid, boolean hasGas) {
        var material = new Material.Builder(GregicalityModern.id(name)).color(color);
        if (hasPlasma) material.plasma(temperature);
        if (hasLiquid) material.liquid(temperature);
        if (hasGas) material.gas(temperature);
        return material.buildAndRegister();
    }

    private static Material simpleFluidMaterial(String name, int color, boolean hasPlasma, boolean hasLiquid, boolean hasGas) {
        var material = new Material.Builder(GregicalityModern.id(name)).color(color);
        if (hasPlasma) material.plasma();
        if (hasLiquid) material.liquid();
        if (hasGas) material.gas();
        return material.buildAndRegister();
    }

    private static Material simpleFluidMaterial(String name, int color, int temperature, boolean hasPlasma, boolean hasLiquid, boolean hasGas, String formula) {
        return simpleFluidMaterial(name, color, temperature, hasPlasma, hasLiquid, hasGas).setFormula(formula, true);
    }

    private static Material simpleFluidMaterial(String name, int color, boolean hasPlasma, boolean hasLiquid, boolean hasGas, String formula) {
        return simpleFluidMaterial(name, color, hasPlasma, hasLiquid, hasGas).setFormula(formula, true);
    }

    private static Material simpleFluidMaterial(String name, int color, String formula) {
        return simpleFluidMaterial(name, color, false, true, false, formula);
    }

    private static Material simpleFluidMaterial(String name, int color, MaterialStack... components) {
        return simpleFluidMaterial(name, color, false, true, false).setComponents(components);
    }

